import { Container } from "./styles";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

const Header = () => {
  const navigate = useNavigate();

  let [isHome, setUrlCheckIsHome] = useState(true);

  useEffect(() => {
    const isHomecheck = window.location.href.toString().indexOf("/song") != -1;
    if (isHomecheck) {
      setUrlCheckIsHome(true);
    } else {
      setUrlCheckIsHome(false);
    }
  }, []);

  return (
    <Container>
      <img
        className="logo"
        src="/images/logomcc.jpeg"
        alt="mcc logo"
        onClick={() => navigate("/")}
      />

      {!isHome && <button className="">Login</button>}

      {isHome && (
        <div className="IconsContainer">
          <IconButton
            hasFile={louvor.linkPdfLyrics}
            iconName={"LuType"}
            size={16}
            href={"/song"}
            id={louvor.songBookMapId}
          ></IconButton>
          <IconButton
            hasFile={louvor.linkChords}
            iconName={"LuListMusic"}
            size={17}
            href={"/song"}
            id={louvor.songBookMapId}
          ></IconButton>
          <IconButton
            hasFile={louvor.linkSheetMusic}
            iconName={"LuMusic"}
            size={16}
            href={"/song"}
            id={louvor.songBookMapId}
          ></IconButton>
          <IconButton
            hasFile={louvor.linkAudioFile}
            iconName={"LuVolume1"}
            size={18}
            href={"/song"}
            id={louvor.songBookMapId}
          ></IconButton>
        </div>
      )}
    </Container>
  );
};

export default Header;
