import { Container, Mobile, Desktop, MobileList } from "./styles";

import { useNavigate } from "react-router-dom";

// import { FaChevronDown, FaChevronUp } from "react-icons/fa";

import { useState, useEffect } from "react";

import { Link } from "react-router-dom";

const Header = () => {
  const navigate = useNavigate();

  let [isHome, setUrlCheckIsHome] = useState(true);

  useEffect(() => {
    const isHomecheck = window.location.href.toString().indexOf("/song") != -1;
    if (isHomecheck) {
      setUrlCheckIsHome(true);
    } else {
      setUrlCheckIsHome(false);
    }
  }, []);

  return (
    <Container>
      <img
        className="logo"
        src="/images/logomcc.jpeg"
        alt="mcc logo"
        onClick={() => navigate("/")}
      />

      {!isHome && <button className="">Login</button>}
    </Container>
  );
};

export default Header;
