import styled from "styled-components";

export const Container = styled.div`
  .main-container {
    display: flex;
    align-items: center;
    flex-direction: column;
  }

  .title {
    color: black;
    font-size: 1.5rem;
    margin: 15px 0;
  }

  .filter {
    width: 40%;
    margin-bottom: 30px;
  }

  .praises-container {
    width: 80%;
  }

  .praise-container {
    display: flex;
    justify-content: space-between;
    padding: 20px;
    border: 1px solid gray;
  }

  .praise-title {
    display: flex;
  }

  .praise-title-en {
    color: black;
    font-size: 1.2rem;
    margin-right: 20px;
  }

  .praise-title-pt {
    font-size: 1.2rem;
    color: gray;
  }

  .theme-tag {
    display: flex;
    align-items: center;
    background: red;
    border-radius: 20px;
    font-size: 12px;
    text-align: center;
    padding: 3px 10px;
  }

  .icons-container {
    display: flex;
    align-items: center;
    cursor: pointer;
  }

  .icon-container {
    padding: 10px;
  }
`;

