import { Container } from "./styles";
import { useNavigate, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { LuType, LuListMusic, LuMusic, LuVolume1 } from "react-icons/lu";

const Header = () => {
  const navigate = useNavigate();
  const [louvores, setLouvores] = useState([]);
  const url = "https://mccapi.up.railway.app/SongBookMap";

  let [isHome, setUrlCheckIsHome] = useState(true);

  useEffect(() => {
    const isHomecheck =
      window.location.href.toString().indexOf("/praise") != -1;
    if (isHomecheck) {
      setUrlCheckIsHome(true);
    } else {
      setUrlCheckIsHome(false);
    }

    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setLouvores(data);
      })
      .catch((err) => console.error(err));
  }, []);

  return (
    <Container>
      <img
        className="logo"
        src="/images/logomcc.jpeg"
        alt="mcc logo"
        onClick={() => navigate("/")}
      />

      {!isHome && <button className="">Login</button>}

      {isHome && (
        <div className="icons-container">
          <Link
            to={"/praise"}
            className="icon-container"
            state={{ id: louvor.songBookMapId, iconName: "LuListMusic" }}
          >
            <LuType color="black" size={17} />
          </Link>

          <Link
            to={"/praise"}
            className="icon-container"
            state={{ id: louvor.songBookMapId, iconName: "LuListMusic" }}
          >
            <LuListMusic color="black" size={17} />
          </Link>

          <Link
            to={"/praise"}
            className="icon-container"
            state={{ id: louvor.songBookMapId, iconName: "LuListMusic" }}
          >
            <LuMusic color="black" size={16} />
          </Link>

          <Link
            to={"/praise"}
            className="icon-container"
            state={{ id: louvor.songBookMapId, iconName: "LuListMusic" }}
          >
            <LuVolume1 color="black" size={18} />
          </Link>
        </div>
      )}
    </Container>
  );
};

export default Header;
