import React from "react";
import axios from "axios";
import { Container } from "./styles";
import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Header from "../../components/Header";

export default function Praise() {
  let [louvor, setLouvor] = useState({});
  let [iframeUrl, setIframeUrl] = useState("");
  const [imageSrc, setImageSrc] = useState("");

  const location = useLocation();
  const { id, iconName } = location.state;
  const url = "https://mccapi.up.railway.app/SongBookMap/" + id + "/Get";

  const activeTab = iconName;

  const loadEvent = async (e) => {
    const result = await axios.get(
      "https://mccapi.up.railway.app/SongBookMap/" + id + "/Get"
    );
    setImageSrc("data:image/jpeg;base64," + result.data.eventFlyer);
  };

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setLouvor(data);
        if (activeTab === "LuType") {
          setIframeUrl(data.linkPdfLyrics);
        } else if (activeTab === "LuListMusic") {
          setIframeUrl(data.linkChords);
        } else if (activeTab === "LuMusic") {
          setIframeUrl(data.linkSheetMusic);
        } else if (activeTab === "LuVolume1") {
          setIframeUrl(data.linkAudioFile);
        }
      })
      .catch((err) => console.error(err));
  }, [iconName]);

  console.log("louvor", louvor);

  return (
    <Container>
      <Header />
      <img src={imageSrc} />
      {/* <div className="iframe">
        <iframe
          // src="https://drive.google.com/file/d/1CuanNm1gpo8hTAlgsazzYC-d9lNV-kcB/preview"
          src={iframeUrl}
          width="100%"
          height="100%"
          allow="autoplay"
        ></iframe>
      </div> */}
    </Container>
  );
}
