import { Container, Mobile, Desktop, MobileList } from "./styles";

import { useNavigate } from "react-router-dom";

// import { FaChevronDown, FaChevronUp } from "react-icons/fa";

import { useState, useEffect } from "react";

import { Link } from "react-router-dom";

const Header = () => {
  const navigate = useNavigate();

  let [isHome, setUrlCheckIsHome] = useState(true);

  useEffect(() => {
    const isHomecheck = window.location.href.toString().indexOf("/song") != -1;
    if (isHomecheck) {
      setUrlCheckIsHome(true);
    } else {
      setUrlCheckIsHome(false);
    }
  }, []);
  //   const [icon, setIcon] = useState(true);

  //   const handleIcon = () => {
  //     setIcon(!icon);
  //   };

  return (
    <Container>
      <img
        className="logo"
        src="/images/logomcc.jpeg"
        alt="mcc logo"
        onClick={() => navigate("/")}
      />

      {!isHome && (
        <button className="bg-transparent hover:bg-red-500 text-red-700 hover:text-white py-1 px-4 border rounded-lg border-red-500 hover:border-transparent">
          Login
        </button>
      )}

      {/* <div className="item-menu">
        <p onClick={() => history.push("/login-ong")}>Sou ONG</p>
      </div>
      <div className="item-menu item-menu-2">
        <p onClick={() => history.push("/login-voluntario")}>Sou volunt√°rio</p>
      </div> */}
    </Container>
  );
};

export default Header;
