import { Container } from "./styles";
import Header from "../../components/Header";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { LuType, LuListMusic, LuMusic, LuVolume1 } from "react-icons/lu";
import api from "../../services/api";

const PraisesList = () => {
  const [louvores, setLouvores] = useState([]);
  const url = "https://mccapi.up.railway.app/SongBookMap";
  const [filteredLouvores, setFilteredLouvores] = useState([]);

  function naturalCompare(a, b) {
    let ax = [],
      bx = [];

    a.replace(/(\d+)|(\D+)/g, function (_, $1, $2) {
      ax.push([$1 || Infinity, $2 || ""]);
    });
    b.replace(/(\d+)|(\D+)/g, function (_, $1, $2) {
      bx.push([$1 || Infinity, $2 || ""]);
    });

    while (ax.length && bx.length) {
      var an = ax.shift();
      var bn = bx.shift();
      var nn = an[0] - bn[0] || an[1].localeCompare(bn[1]);
      if (nn) return nn;
    }

    return ax.length - bx.length;
  }

  const handleFilter = (event) => {
    const value = event.target.value;
    const filtered = louvores.filter(
      (louvor) =>
        louvor.englishSongBookNumber
          .toLowerCase()
          .includes(value.toString().toLowerCase()) ||
        louvor.englishTitle
          .toLowerCase()
          .includes(value.toString().toLowerCase()) ||
        louvor.portugueseSongBookNumber
          .toString()
          .toLowerCase()
          .includes(value.toString().toLowerCase()) ||
        louvor.portugueseTitle
          .toLowerCase()
          .includes(value.toString().toLowerCase()) ||
        louvor.theme
          ?.toString()
          .toLowerCase()
          .includes(value.toString().toLowerCase())
    );

    if (value === "") setFilteredLouvores(louvores);
    else setFilteredLouvores(filtered);
  };

  useEffect(() => {
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        data.sort((a, b) => {
          return naturalCompare(
            a.portugueseSongBookNumber,
            b.portugueseSongBookNumber
          );
        });
        setLouvores(data);
        setFilteredLouvores(data);
      })
      .catch((err) => console.error(err));
  }, [filteredLouvores]);

  return (
    <Container>
      <Header />
      <main className="">
        <div className="">
          <div className="">
            <h5 className="">Praise songs</h5>
          </div>
          <input
            type="text"
            id="filter"
            onChange={handleFilter}
            className=""
            placeholder="Search..."
          />
          <div className="">
            {filteredLouvores.map((louvor, index) => (
              <div className="" key={index}>
                <div className="flex items-center gap-x-3">
                  <div>
                    {louvor.englishTitle && (
                      <div className="">
                        <h6 className="">
                          {louvor.englishSongBookNumber
                            ? louvor.englishSongBookNumber + " - "
                            : " "}
                          {louvor.englishTitle ? louvor.englishTitle : ""}
                        </h6>
                        <div className=""> {louvor.theme}</div>
                      </div>
                    )}
                    {louvor.portugueseTitle && (
                      <p className="">
                        {louvor.portugueseSongBookNumber
                          ? louvor.portugueseSongBookNumber + " - "
                          : "Avulso - "}
                        {louvor.portugueseTitle ? louvor.portugueseTitle : ""}
                      </p>
                    )}
                  </div>
                </div>

                <div className="icons-container">
                  <Link href={"/song"} className="icon-container">
                    <LuType color="black" size={17} />
                  </Link>

                  <Link href={"/song"} className="icon-container">
                    <LuListMusic color="black" size={17} />
                  </Link>

                  <Link href={"/song"} className="icon-container">
                    <LuMusic color="black" size={16} />
                  </Link>

                  <Link href={"/song"} className="icon-container">
                    <LuVolume1 color="black" size={18} />
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </main>
    </Container>
  );
};

export default PraisesList;

